mapper.select:
SELECT h.send_code AS sendCode, h.stage_code AS stageCode, s.stage_name_kor AS stageNameKor, h.template_code AS templateCode, mt.title AS templateTitle, h.reservation_code AS reservationCode, h.stay_code AS stayCode, h.status AS status, h.scheduled_at AS scheduledAt, h.sent_at AS sentAt, h.fail_reason AS failReason, h.external_message_id AS externalMessageId, p.property_code AS propertyCode FROM message_send_history h JOIN message_journey_stage s ON h.stage_code = s.stage_code JOIN message_template mt ON h.template_code = mt.template_code AND h.stage_code = mt.stage_code
LEFT JOIN reservation r ON h.reservation_code = r.reservation_code LEFT JOIN stay st ON h.stay_code = st.stay_code LEFT JOIN reservation r2 ON st.reservation_code = r2.reservation_code
JOIN property p ON p.property_code = COALESCE(r.property_code, r2.property_code)
WHERE p.hotel_group_code = #{search.hotelGroupCode}
AND p.property_code = #{search.propertyCode}
AND h.stage_code = #{search.stageCode}
AND h.reservation_code = #{search.reservationCode}
AND h.stay_code = #{search.stayCode}
AND h.status = #{search.status}
AND h.sent_at >= #{search.fromDate}
AND h.sent_at <= #{search.toDate}
ORDER BY ${sort.sortBy} ${sort.direction} LIMIT #{page.size} OFFSET #{page.offset}

SELECT COUNT(*)
FROM (
SELECT h.send_code
FROM message_send_history h
JOIN message_journey_stage s
ON h.stage_code = s.stage_code
JOIN message_template mt
ON h.template_code = mt.template_code
AND h.stage_code   = mt.stage_code

LEFT JOIN reservation r
ON h.reservation_code = r.reservation_code
LEFT JOIN stay st
ON h.stay_code = st.stay_code
LEFT JOIN reservation r2
ON st.reservation_code = r2.reservation_code

JOIN property p
ON p.property_code = COALESCE(r.property_code, r2.property_code)

WHERE p.hotel_group_code = #{search.hotelGroupCode}


    AND p.property_code = #{search.propertyCode}



    AND h.stage_code = #{search.stageCode}



    AND h.reservation_code = #{search.reservationCode}



    AND h.stay_code = #{search.stayCode}



    AND h.status = #{search.status}



    AND h.sent_at  >=  #{search.fromDate}



    AND h.sent_at  <=  #{search.toDate}

) t
