mapper.select:
SELECT p.property_code AS propertyCode, p.property_city AS propertyCity, p.property_name AS propertyName, p.property_status AS propertyStatus, hg.hotel_group_name AS hotelGroupName FROM property p JOIN hotel_group hg ON hg.hotel_group_code=p.hotel_group_code
AND p.property_code = #{search.propertyCode}
AND p.property_city LIKE CONCAT('%', #{search.propertyCity}, '%')
AND p.property_name LIKE CONCAT('%', #{search.propertyName}, '%')
AND p.property_status = #{search.propertyStatus}
ORDER BY ${sort.sortBy} ${sort.direction}
ORDER BY p.property_code DESC
LIMIT #{page.size} OFFSET #{page.offset}

SELECT COUNT(*) FROM property p JOIN hotel_group hg ON hg.hotel_group_code= p.hotel_group_code
AND p.property_code = #{search.propertyCode}
AND p.property_city LIKE CONCAT('%', #{search.propertyCity}, '%')
AND p.property_name LIKE CONCAT('%', #{search.propertyName}, '%')
AND p.property_status = #{search.propertyStatus}
