mapper.select:
SELECT
  ll.login_log_code AS loginLogCode,
  ll.action AS action,
  ll.employee_code AS employeeCode,
  e.employee_name_enc AS employeeName,
  e.login_id AS loginId,
  ll.user_ip AS userIp,
  ll.occurred_at AS occurredAt,
  ll.result AS result,
  ll.failed_reason AS failedReason,
  ll.hotel_group_code AS hotelGroupCode
FROM login_log ll
JOIN employee e ON ll.employee_code = e.employee_code



  AND ll.hotel_group_code = #{hotelGroupCode}


    AND ll.employee_code = #{search.employeeCode}



    AND e.login_id LIKE CONCAT('%', #{search.loginId}, '%')



    AND ll.action = #{search.action}



    AND ll.result = #{search.result}



    AND ll.user_ip LIKE CONCAT('%', #{search.userIp}, '%')



    AND ll.occurred_at  >=  #{search.fromDate}



    AND ll.occurred_at  <=  #{search.toDate}





    ORDER BY ${sort.sortBy} ${sort.direction}


    ORDER BY ll.occurred_at DESC


LIMIT #{page.size} OFFSET #{page.offset}

SELECT COUNT(*)
FROM login_log ll
JOIN employee e ON ll.employee_code = e.employee_code


  AND ll.hotel_group_code = #{hotelGroupCode}


    AND ll.employee_code = #{search.employeeCode}



    AND e.login_id LIKE CONCAT('%', #{search.loginId}, '%')



    AND ll.action = #{search.action}



    AND ll.result = #{search.result}



    AND ll.user_ip LIKE CONCAT('%', #{search.userIp}, '%')



    AND ll.occurred_at  >=  #{search.fromDate}



    AND ll.occurred_at  <=  #{search.toDate}
