mapper.select:
SELECT
h.hotel_group_code AS hotelGroupCode,
h.hotel_expired_at AS hotelExpiredAt,
h.hotel_group_name AS hotelGroupName
FROM hotel_group h



  AND h.hotel_group_code = #{search.hotelGroupCode}



    AND h.hotel_group_name LIKE CONCAT('%', #{search.hotelGroupName}, '%')





    ORDER BY ${sort.sortBy} ${sort.direction}


    ORDER BY h.hotel_expired_at DESC


LIMIT #{page.size} OFFSET #{page.offset}

SELECT COUNT(*)
FROM hotel_group h



    AND h.hotel_group_code = #{search.hotelGroupCode}



    AND h.hotel_group_name = #{search.hotelGroupName}
