sql.where:
p.hotel_group_code = #{search.hotelGroupCode}


    AND i.customer_code = #{search.customerCode}



    AND i.property_code = #{search.propertyCode}



    AND i.inquiry_category_code = #{search.inquiryCategoryCode}



    AND i.inquiry_status = #{search.status}







                AND cu.customer_name_hash = #{search.customerNameHash}

            AND 1=0







                AND e.employee_name_hash =
                #{search.employeeNameHash, jdbcType=VARBINARY, typeHandler=org.apache.ibatis.type.ByteArrayTypeHandler}

            AND 1=0







                AND e.login_id = #{search.employeeLoginId}

            AND 1=0







                AND i.inquiry_title LIKE CONCAT('%', #{search.keyword}, '%')

            AND 1=0






            AND (
            i.inquiry_title LIKE CONCAT('%', #{search.keyword}, '%')
            OR i.inquiry_content LIKE CONCAT('%', #{search.keyword}, '%')


                OR cu.customer_name_hash = #{search.customerNameHash}



                OR e.employee_name_hash =
                #{search.employeeNameHash, jdbcType=VARBINARY, typeHandler=org.apache.ibatis.type.ByteArrayTypeHandler}



                OR e.login_id = #{search.employeeLoginId}

            )






    AND i.created_at >= CONCAT(#{search.fromDate}, ' 00:00:00')



    AND i.created_at < DATE_ADD(CONCAT(#{search.toDate}, ' 00:00:00'), INTERVAL 1 DAY)
